// this class retrieve data from object tree generated by xml2js 
class DBusInterfaceDefinition {
  constructor (data) {
    this.data = data
    Object.defineProperty(this, 'name', {
      get () {
        return this.data.$.name
      }
    })
  }

  // return a signature
  method (name) {
    if (this.i && this.i.method) {
      let m = this.i.method.find(o => o.$.name === name)
      if (m) return m.arg.map(x => x.$)
    }
  }

  signal (name) {
  }

  property (name) {
  }

  // return [[name, sig]]
  properties () {
    
  }
}

module.exports = DBusInterfaceDefinition
